import React, { useState, useEffect } from 'react';
import { fetchFromSpotify } from './spotifyAuth';

export default function Discovery({ token }) {
  const [recommendations, setRecommendations] = useState([]);
  const [mood, setMood] = useState('happy');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecommendations();
  }, [token, mood]);

  const fetchRecommendations = async () => {
    setLoading(true);
    try {
      const topTracks = await fetchFromSpotify('/me/top/tracks?limit=5', token);
      const seedTracks = topTracks.items.map(track => track.id).join(',');
      const data = await fetchFromSpotify(`/recommendations?seed_tracks=${seedTracks}&target_valence=${mood === 'happy' ? 0.8 : 0.2}`, token);
      setRecommendations(data.tracks || []);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setRecommendations([]);
    }
    setLoading(false);
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Descubre Nueva Música</h2>
      <div className="mb-4">
        <label className="mr-2">Estado de ánimo:</label>
        <select value={mood} onChange={(e) => setMood(e.target.value)} className="bg-gray-800 p-2 rounded">
          <option value="happy">Feliz</option>
          <option value="sad">Triste</option>
        </select>
      </div>
      {loading ? (
        <p>Cargando recomendaciones...</p>
      ) : recommendations.length > 0 ? (
        <ul className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {recommendations.map(track => (
            <li key={track.id} className="bg-gray-800 p-4 rounded">
              <img src={track.album.images[1]?.url} alt={track.name} className="w-full mb-2" />
              <p className="font-bold">{track.name}</p>
              <p>{track.artists[0].name}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No se encontraron recomendaciones.</p>
      )}
    </div>
  );
}
