import React from 'react';
import { Line } from 'react-chartjs-2';

export default function HistoricalRankingModal({ isOpen, onClose, rankingData, title, timeRange }) {
  if (!isOpen) return null;

  const chartData = {
    labels: rankingData.map(data => data.date), // fechas del historial
    datasets: [
      {
        label: title,
        data: rankingData.map(data => data.rank), // posiciones en el historial
        borderColor: '#1DB954',
        backgroundColor: 'rgba(29, 185, 84, 0.3)',
      }
    ],
  };

  const chartOptions = {
    scales: {
      y: {
        reverse: true,
        ticks: {
          stepSize: 5,
        }
      },
    },
    plugins: {
      legend: { display: false },
    },
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-gray-900 p-8 rounded-lg shadow-lg text-white w-3/4 max-w-lg">
        <h2 className="text-2xl font-bold mb-4">Historical ranking data</h2>
        <p className="text-sm mb-4">
          This graph shows the placement of the selected {title.toLowerCase()} over time in the "{timeRange}" period.
        </p>
        <Line data={chartData} options={chartOptions} />
        <div className="flex justify-end mt-6 space-x-4">
          <button className="px-4 py-2 bg-[#1DB954] rounded text-white" onClick={() => window.open(rankingData.spotifyUrl, '_blank')}>
            Open in Spotify
          </button>
          <button className="px-4 py-2 bg-gray-700 rounded text-white" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
}
