import React, { useState, useEffect } from 'react';
import { fetchFromSpotify } from './spotifyAuth';

export default function Playlists({ token }) {
  const [playlists, setPlaylists] = useState([]);
  const [selectedPlaylist, setSelectedPlaylist] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetchFromSpotify('/me/playlists', token)
      .then(data => {
        setPlaylists(data.items || []);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching playlists:', error);
        setLoading(false);
      });
  }, [token]);

  const handlePlaylistClick = async (playlistId) => {
    const data = await fetchFromSpotify(`/playlists/${playlistId}`, token);
    setSelectedPlaylist(data);
  };

  if (loading) {
    return <div>Loading playlists...</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
      <div>
        <h2 className="text-2xl font-bold mb-4">Tus Playlists</h2>
        {playlists.length > 0 ? (
          <ul className="space-y-2">
            {playlists.map(playlist => (
              <li key={playlist.id} className="cursor-pointer hover:bg-gray-800 p-2 rounded" onClick={() => handlePlaylistClick(playlist.id)}>
                {playlist.name}
              </li>
            ))}
          </ul>
        ) : (
          <p>No se encontraron playlists.</p>
        )}
      </div>
      {selectedPlaylist && (
        <div>
          <h2 className="text-2xl font-bold mb-4">{selectedPlaylist.name}</h2>
          {selectedPlaylist.tracks.items.length > 0 ? (
            <ul className="space-y-2">
              {selectedPlaylist.tracks.items.map(item => (
                <li key={item.track.id} className="flex items-center space-x-2">
                  <img src={item.track.album.images[2]?.url} alt={item.track.name} className="w-10 h-10" />
                  <span>{item.track.name} - {item.track.artists[0].name}</span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Esta playlist está vacía.</p>
          )}
        </div>
      )}
    </div>
  );
}
