import React, { useState, useEffect } from 'react';
import { fetchFromSpotify } from '../../spotifyAuth';

export default function Statistics({ token }) {
  const [topTracks, setTopTracks] = useState([]);
  const [topArtists, setTopArtists] = useState([]);
  const [topAlbums, setTopAlbums] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('short_term');

  useEffect(() => {
    fetchTopItems();
  }, [token, timeRange]);

  const fetchTopItems = async () => {
    setLoading(true);
    try {
      const tracksData = await fetchFromSpotify(`/me/top/tracks?limit=10&time_range=${timeRange}`, token);
      setTopTracks(tracksData.items || []);

      const artistsData = await fetchFromSpotify(`/me/top/artists?limit=10&time_range=${timeRange}`, token);
      setTopArtists(artistsData.items || []);

      const genres = artistsData.items.flatMap(artist => artist.genres);
      const genreCounts = genres.reduce((acc, genre) => {
        acc[genre] = (acc[genre] || 0) + 1;
        return acc;
      }, {});
      const sortedGenres = Object.entries(genreCounts)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5);
      setTopGenres(sortedGenres);

      const albumsData = tracksData.items.map(track => track.album);
      const albumCounts = albumsData.reduce((acc, album) => {
        acc[album.id] = acc[album.id] || { ...album, count: 0 };
        acc[album.id].count++;
        return acc;
      }, {});
      const sortedAlbums = Object.values(albumCounts)
        .sort((a, b) => b.count - a.count)
        .slice(0, 5);
      setTopAlbums(sortedAlbums);

    } catch (error) {
      console.error('Error fetching top items:', error);
      setTopTracks([]);
      setTopArtists([]);
      setTopGenres([]);
      setTopAlbums([]);
    }
    setLoading(false);
  };

  const handleTimeRangeChange = (range) => {
    setTimeRange(range);
  };

  if (loading) {
    return <div className="text-center text-lg font-semibold">Cargando estadísticas...</div>;
  }

  return (
    <div className="p-6">
      <div className="flex justify-center space-x-4 mb-8">
        <button
          onClick={() => handleTimeRangeChange('short_term')}
          className={`px-4 py-2 rounded ${timeRange === 'short_term' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
        >
          Últimas 4 semanas
        </button>
        <button
          onClick={() => handleTimeRangeChange('medium_term')}
          className={`px-4 py-2 rounded ${timeRange === 'medium_term' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
        >
          Últimos 6 meses
        </button>
        <button
          onClick={() => handleTimeRangeChange('long_term')}
          className={`px-4 py-2 rounded ${timeRange === 'long_term' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
        >
          Todo el año
        </button>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        <div>
          <h2 className="text-2xl font-bold mb-4">Top Canciones</h2>
          <ol className="space-y-4">
            {topTracks.map(track => (
              <li key={track.id} className="flex items-center space-x-3">
                <img src={track.album.images[0].url} alt={track.name} className="w-12 h-12 rounded" />
                <div>
                  <span className="block font-semibold">{track.name}</span>
                  <span className="text-gray-500">{track.artists[0].name}</span>
                </div>
              </li>
            ))}
          </ol>
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-4">Top Artistas</h2>
          <ol className="space-y-4">
            {topArtists.map(artist => (
              <li key={artist.id} className="flex items-center space-x-3">
                <img src={artist.images[0]?.url} alt={artist.name} className="w-12 h-12 rounded" />
                <span className="font-semibold">{artist.name}</span>
              </li>
            ))}
          </ol>
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-4">Top Álbumes</h2>
          <ol className="space-y-4">
            {topAlbums.map(album => (
              <li key={album.id} className="flex items-center space-x-3">
                <img src={album.images[0].url} alt={album.name} className="w-12 h-12 rounded" />
                <div>
                  <span className="block font-semibold">{album.name}</span>
                  <span className="text-gray-500">{album.artists[0].name}</span>
                </div>
              </li>
            ))}
          </ol>
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-4">Top Géneros</h2>
          <ol className="space-y-2">
            {topGenres.map(([genre, count]) => (
              <li key={genre} className="text-gray-800">
                {genre} ({count} artistas)
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
}
