import React, { useState, useEffect } from 'react';
import { fetchFromSpotify } from '../../spotifyAuth';

export default function Statistics({ token }) {
  const [topTracks, setTopTracks] = useState([]);
  const [topArtists, setTopArtists] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTopItems();
  }, [token]);

  const fetchTopItems = async () => {
    setLoading(true);
    try {
      const tracksData = await fetchFromSpotify('/me/top/tracks?limit=10', token);
      setTopTracks(tracksData.items || []);

      const artistsData = await fetchFromSpotify('/me/top/artists?limit=10', token);
      setTopArtists(artistsData.items || []);

      const genres = artistsData.items.flatMap(artist => artist.genres);
      const genreCounts = genres.reduce((acc, genre) => {
        acc[genre] = (acc[genre] || 0) + 1;
        return acc;
      }, {});
      const sortedGenres = Object.entries(genreCounts)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5);
      setTopGenres(sortedGenres);
    } catch (error) {
      console.error('Error fetching top items:', error);
      setTopTracks([]);
      setTopArtists([]);
      setTopGenres([]);
    }
    setLoading(false);
  };

  if (loading) {
    return <div>Cargando estadísticas...</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
      <div>
        <h2 className="text-2xl font-bold mb-4">Top Canciones</h2>
        {topTracks.length > 0 ? (
          <ol className="list-decimal list-inside space-y-2">
            {topTracks.map(track => (
              <li key={track.id}>{track.name} - {track.artists[0].name}</li>
            ))}
          </ol>
        ) : (
          <p>No se encontraron canciones top.</p>
        )}
      </div>
      <div>
        <h2 className="text-2xl font-bold mb-4">Top Artistas</h2>
        {topArtists.length > 0 ? (
          <ol className="list-decimal list-inside space-y-2">
            {topArtists.map(artist => (
              <li key={artist.id}>{artist.name}</li>
            ))}
          </ol>
        ) : (
          <p>No se encontraron artistas top.</p>
        )}
      </div>
      <div>
        <h2 className="text-2xl font-bold mb-4">Top Géneros</h2>
        {topGenres.length > 0 ? (
          <ol className="list-decimal list-inside space-y-2">
            {topGenres.map(([genre, count]) => (
              <li key={genre}>{genre} ({count} artistas)</li>
            ))}
          </ol>
        ) : (
          <p>No se encontraron géneros top.</p>
        )}
      </div>
    </div>
  );
}
