import React, { useState, useEffect } from 'react';
import { fetchFromSpotify } from '../../spotifyAuth';

export default function Statistics({ token }) {
  const [topTracks, setTopTracks] = useState([]);
  const [topArtists, setTopArtists] = useState([]);
  const [topAlbums, setTopAlbums] = useState([]);
  const [topGenres, setTopGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('short_term');
  const [playingTrack, setPlayingTrack] = useState(null);

  useEffect(() => {
    fetchTopItems();
  }, [token, timeRange]);

  const fetchTopItems = async () => {
    setLoading(true);
    try {
      const tracksData = await fetchFromSpotify(`/me/top/tracks?limit=10&time_range=${timeRange}`, token);
      setTopTracks(tracksData.items || []);

      const artistsData = await fetchFromSpotify(`/me/top/artists?limit=10&time_range=${timeRange}`, token);
      setTopArtists(artistsData.items || []);

      const genres = artistsData.items.flatMap(artist => artist.genres);
      const genreCounts = genres.reduce((acc, genre) => {
        acc[genre] = (acc[genre] || 0) + 1;
        return acc;
      }, {});
      const sortedGenres = Object.entries(genreCounts)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5);
      setTopGenres(sortedGenres);

      const albumsData = tracksData.items.map(track => track.album);
      const albumCounts = albumsData.reduce((acc, album) => {
        acc[album.id] = acc[album.id] || { ...album, count: 0 };
        acc[album.id].count++;
        return acc;
      }, {});
      const sortedAlbums = Object.values(albumCounts)
        .sort((a, b) => b.count - a.count)
        .slice(0, 10);
      setTopAlbums(sortedAlbums);
    } catch (error) {
      console.error('Error fetching top items:', error);
      setTopTracks([]);
      setTopArtists([]);
      setTopGenres([]);
      setTopAlbums([]);
    }
    setLoading(false);
  };

  const handleTimeRangeChange = (range) => {
    setTimeRange(range);
  };

  const togglePlay = (trackUrl) => {
    if (playingTrack && playingTrack.src === trackUrl) {
      playingTrack.pause();
      setPlayingTrack(null);
    } else {
      if (playingTrack) {
        playingTrack.pause();
      }
      const newTrack = new Audio(trackUrl);
      newTrack.play();
      setPlayingTrack(newTrack);
    }
  };

  if (loading) {
    return <div className="text-center text-lg font-semibold">Cargando estadísticas...</div>;
  }

  return (
    <div className="p-8 bg-gray-900 min-h-screen text-white">
      <div className="flex justify-center space-x-4 mb-12">
        <button
          onClick={() => handleTimeRangeChange('short_term')}
          className={`px-5 py-2 rounded-full ${timeRange === 'short_term' ? 'bg-blue-500 text-white shadow-lg' : 'bg-gray-700 hover:bg-gray-600'}`}
        >
          Últimas 4 semanas
        </button>
        <button
          onClick={() => handleTimeRangeChange('medium_term')}
          className={`px-5 py-2 rounded-full ${timeRange === 'medium_term' ? 'bg-blue-500 text-white shadow-lg' : 'bg-gray-700 hover:bg-gray-600'}`}
        >
          Últimos 6 meses
        </button>
        <button
          onClick={() => handleTimeRangeChange('long_term')}
          className={`px-5 py-2 rounded-full ${timeRange === 'long_term' ? 'bg-blue-500 text-white shadow-lg' : 'bg-gray-700 hover:bg-gray-600'}`}
        >
          Todo el año
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 justify-center">
        <div className="rounded-lg shadow-lg p-6 bg-gray-800 hover:bg-gray-700 transition-all duration-200 ease-in-out">
          <h2 className="text-xl font-bold mb-4">Top Canciones</h2>
          <ol className="space-y-4">
            {topTracks.map(track => (
              <li
                key={track.id}
                className="flex items-center space-x-3 hover:bg-gray-600 p-3 rounded-lg transition-transform duration-200 ease-in-out transform hover:scale-105"
              >
                <img src={track.album.images[0].url} alt={track.name} className="w-16 h-16 rounded-lg shadow-md" />
                <div className="flex-1">
                  <span className="block font-semibold text-white">{track.name}</span>
                  <span className="text-gray-400 text-sm">{track.artists[0].name}</span>
                </div>
                {track.preview_url && (
                  <button
                    onClick={() => togglePlay(track.preview_url)}
                    className="px-3 py-1 bg-[#1DB954] rounded-full text-white font-semibold hover:bg-blue-600 focus:outline-none"
                  >
                    {playingTrack && playingTrack.src === track.preview_url && !playingTrack.paused ? 'Pause' : 'Play'}
                  </button>
                )}
              </li>
            ))}
          </ol>
        </div>

        <div className="rounded-lg shadow-lg p-6 bg-gray-800 hover:bg-gray-700 transition-all duration-200 ease-in-out">
          <h2 className="text-xl font-bold mb-4">Top Artistas</h2>
          <ol className="space-y-4">
            {topArtists.map(artist => (
              <li key={artist.id} className="flex items-center space-x-3">
                <img src={artist.images[0]?.url} alt={artist.name} className="w-16 h-16 rounded-full shadow-md" />
                <span className="font-semibold text-white">{artist.name}</span>
              </li>
            ))}
          </ol>
        </div>

        <div className="rounded-lg shadow-lg p-6 bg-gray-800 hover:bg-gray-700 transition-all duration-200 ease-in-out">
          <h2 className="text-xl font-bold mb-4">Top Álbumes</h2>
          <ol className="space-y-4">
            {topAlbums.map(album => (
              <li key={album.id} className="flex items-center space-x-3">
                <img src={album.images[0].url} alt={album.name} className="w-16 h-16 rounded-lg shadow-md" />
                <div>
                  <span className="block font-semibold text-white">{album.name}</span>
                  <span className="text-gray-400 text-sm">{album.artists[0].name}</span>
                </div>
              </li>
            ))}
          </ol>
        </div>

        <div className="rounded-lg shadow-lg p-6 bg-gray-800 hover:bg-gray-700 transition-all duration-200 ease-in-out">
          <h2 className="text-xl font-bold mb-4">Top Géneros</h2>
          <ol className="space-y-2">
            {topGenres.map(([genre, count]) => (
              <li key={genre} className="text-gray-400 text-sm font-medium">
                {genre} ({count} artistas)
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
}
