import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, TimeScale, LinearScale, PointElement, LineElement, Tooltip, Legend, Title } from 'chart.js';
import 'chartjs-adapter-date-fns';

// Registra los componentes que vas a usar
ChartJS.register(TimeScale, LinearScale, PointElement, LineElement, Tooltip, Legend, Title);

export default function HistoricalRankingModal({ dataPoints, onClose }) {
  // Datos del gráfico (asegúrate de ajustar los datos según tu necesidad)
  const data = {
    labels: dataPoints.map(point => point.date),
    datasets: [
      {
        label: 'Ranking Histórico',
        data: dataPoints.map(point => point.rank),
        borderColor: '#1DB954',
        backgroundColor: 'rgba(29, 185, 84, 0.2)',
        pointBackgroundColor: '#1DB954',
        pointBorderColor: '#1DB954',
        pointHoverBackgroundColor: '#ffffff',
        pointHoverBorderColor: '#1DB954',
        pointRadius: 5,
        pointHoverRadius: 7,
        tension: 0.2, // Suaviza la línea
      },
    ],
  };

  // Opciones de configuración del gráfico
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          tooltipFormat: 'MMM d, yyyy',
          displayFormats: {
            day: 'MM/dd/yyyy',
          },
        },
        title: {
          display: true,
          text: 'Date',
          color: '#ffffff',
          font: {
            size: 14,
          },
        },
        ticks: {
          color: '#b0b0b0',
          font: {
            size: 12,
          },
        },
        grid: {
          color: '#333333',
        },
      },
      y: {
        reverse: true,
        title: {
          display: true,
          text: 'Rank',
          color: '#ffffff',
          font: {
            size: 14,
          },
        },
        ticks: {
          color: '#b0b0b0',
          font: {
            size: 12,
          },
          stepSize: 1,
        },
        grid: {
          color: '#333333',
        },
      },
    },
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        backgroundColor: '#2e2e2e',
        titleColor: '#ffffff',
        bodyColor: '#ffffff',
        titleFont: { size: 14 },
        bodyFont: { size: 12 },
      },
      title: {
        display: true,
        text: 'Historical ranking data',
        color: '#ffffff',
        font: {
          size: 18,
          weight: 'bold',
        },
        padding: {
          top: 10,
          bottom: 30,
        },
      },
    },
  };

  return (
    <div className="modal">
      <h2>Historical ranking data</h2>
      <p>
        This graph shows the placement of the selected track over time in the "last 4 weeks" period.
      </p>
      <div style={{ height: '400px' }}>
        <Line data={data} options={options} />
      </div>
      <button onClick={onClose}>Close</button>
    </div>
  );
}
